import React from "react";
import { useQuery, gql } from "@apollo/client";

export const filesQuery = gql`
  {
    files
  }
`;

export const Files = () => {
  const { data, loading } = useQuery(filesQuery);

  if (loading) {
    return <div>loading...</div>;
  }

  return (
    <div>
      {data.files.map((x) => (
        <img
          style={{ width: 200 }}
          key={x}
          src={`http://localhost:4000/images/${x}`}
          alt={x}
        />
      ))}
    </div>
  );
};
