import React from "react";
import { useQuery, gql} from "@apollo/client";

// import { GET_ALL_FILES } from "../queries";

export const GET_ALL_FILES = gql`
  query {
    files
  }
`;

export const Files = () => {
  const { data, loading } = useQuery(GET_ALL_FILES);
  if (loading) return <p>Loading...</p>;
  console.log(data);

  return (
    <div>
      {data.getAllFiles.map(x => {
        <img style={{width: 200}} key={x} src={`http://localhost:4000/images/${x}`} alt={x} />
      })}
    </div>
  )
};
